mapper.insert:
insert into approvals (
draftId,
templateId,
userId, `order`
) values (
#{draftId},
#{templateId},
#{userId}, #{order}
)

mapper.update:
update approvals
set status = #{status}
where draftId = #{draftId}
  and userId = #{userId}

mapper.select:
select *
from approvals
where templateId = #{templateId}
  and draftId is null;

SELECT userId
FROM approvals
WHERE draftId = #{draftId}
  AND `order` = (SELECT `order` + 1
                 FROM approvals
                 WHERE draftId = #{draftId}
                   AND userId = #{userId}
                   AND status = 'APPROVED')
    LIMIT 1;
