mapper.select:
select *
from users
where departmentId = #{departmentId}
  and isDeleted = 0
    LIMIT #{size}
OFFSET #{offset}

mapper.update:
update users
name = #{name}, email = #{email}, profileImage = #{profileImage}, password = #{password}, addr = #{addr}, phone = #{phone}, departmentId = #{departmentId}, team = #{team}, position = #{position}, updatedAt = now()
where id = #{id}

mapper.select:
SELECT u.*, d.name AS departmentName FROM users AS u JOIN department AS d ON u.departmentId = d.id WHERE u.isDeleted = 1
AND ( u.name LIKE CONCAT('%', #{keyword}, '%') OR d.name LIKE CONCAT('%', #{keyword}, '%') OR u.team LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY u.deletedAt DESC LIMIT #{size} OFFSET #{offset}
